import React from "react";
import { Link } from "react-router-dom";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import "../../page.css";

class ListEquipe extends React.Component {
  constructor() {
    super();
    this.state = {
      data: [],
      emp: []
    };
  }

  fetchData() {
    fetch('http://127.0.0.1:8000/team/')
      .then(response => response.json())
      .then((data) => {
        this.setState({
          data: data,
        });
      });
  }


  

  componentDidMount() {
    this.fetchData();
    this.fetchDataEmp(); // Fetch employee data when the component mounts
  }

  deleteEquipe(id) {
    fetch('http://127.0.0.1:8000/team/' + id + '/', {
      method: 'DELETE',
    })
      .then(response => response)
      .then(() => {
        this.fetchData();
      });
  }

    // Add this function to fetch employee data
    fetchDataEmp() {
        fetch('http://127.0.0.1:8000/employee/')
          .then((response) => response.json())
          .then((data) => {
            
            this.setState({ emp: data }, 
                
            () => {          
              console.log(this.state.emp);
            });
          });
      }
      //*********************** */

  render() {
    const employees = this.state.emp
    console.log(employees)

    return (
      <div>
        <Header />
        <div id='page'>
          <Link className="nav-link" style={{ paddingLeft: '1340px' }} to="/add_équipe">
            <span className="btn btn-success" style={{ fontSize: '27px' }}>Ajouter</span>
          </Link>
          <table className="table table-striped table-bordered text-center">
            <thead>
              <tr>
                <th scope="row">Equipe No</th>
                <th> Nom équipe </th>
                <th> Responsable </th>
                <th> Date du création </th>
                <th> Projet en Cours </th>
                <th> Employées </th>
                <th> Actions </th>
              </tr>
            </thead>
            <tbody>
              









            </tbody>
          </table>
        </div>
        <Footer />
      </div>
    );
  }
}

export default ListEquipe;
