import React, { Component } from "react";
import Select from 'react-select';

class AddEquipe extends Component {
  constructor(props) {
    super(props);
    this.state = {
      nom_eq: "",
      resp_eq: "",
      start_eq: "",
      project_run: "",
      emp_eq: [],
      data: [],
    };
  }

  handleChange = (selectedOptions) => {
    this.setState({ emp_eq: selectedOptions });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    // Vous pouvez envoyer les données à votre backend ici
    const formData = {
      nom_eq: this.state.nom_eq,
      resp_eq: this.state.resp_eq,
      start_eq: this.state.start_eq,
      project_run: this.state.project_run,
      emp_eq: this.state.emp_eq.map(option => option.value), // Extract values from selected options
    };

    console.log(formData);
  };




  submitted(){
    fetch('http://127.0.0.1:8000/team/',
    {
        method:'POST',
        body:JSON.stringify(this.state),
        headers:{'Content-type': 'application/json; charset=UTF-8',}
    }
    )
    console.log(this.state)
    // Redirection vers la page racine
   // this.props.history.push('/list_congés');
    // Rafraîchir la page après la redirection
  // window.location.reload()        
}


  render() {
    const myData = [
      { value: 1, label: 'Option 1' },
      { value: 2, label: 'Option 2' },
      { value: 3, label: 'Option 3' },
    ];

    return (
      <div>
        <h2>Formulaire d'ajout d'une équipe:</h2>
        <form onSubmit={this.handleSubmit}>
          <div>
            <label htmlFor="nom_eq">Nom de l'équipe:</label>
            <input
              name="nom_eq"
              type="text"
              onChange={(e) => this.setState({ nom_eq: e.target.value })}
            />
          </div>
          <div>
            <label htmlFor="resp_eq">Tech lead de l'équipe:</label>
            <input
              name="resp_eq"
              type="text"
              onChange={(e) => this.setState({ resp_eq: e.target.value })}
            />
          </div>
          <div>
            <label htmlFor="start_eq">Entrer la date de création de l'équipe:</label>
            <input
              name="start_eq"
              type="date"
              onChange={(e) => this.setState({ start_eq: e.target.value })}
            />
          </div>
          <div>
            <label htmlFor="project_run">Entrer l'intitulé du projet en cours :</label>
            <input
              name="project_run"
              type="text"
              onChange={(e) => this.setState({ project_run: e.target.value })}
            />
          </div>
          <div>
            <label htmlFor="emp_eq">Entrer les employés membres de l'équipe:</label>
            <Select
              isMulti
              options={myData}
              onChange={this.handleChange}
              value={this.state.emp_eq}
            />
          </div>
          <div>
            <button type="submit">Ajouter une équipe</button>
          </div>
        </form>
      </div>
    );
  }
}

export default AddEquipe;
