import React from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import Header from "../../Header";
class ListTribes extends React.Component{

  constructor(){
    super();
    this.state={
      data:[]
    };
  }

  fetchData(){
    fetch('http://127.0.0.1:8000/tribes/')
    .then(response=>response.json())
    .then((data)=>{
      this.setState({
        data:data 
      });
      console.log(data)
    });
  }

  componentDidMount(){
    this.fetchData();
  }

  deleteEmp(id){
    fetch('http://127.0.0.1:8000/tribes/'+id+'/',{
      method:'DELETE',
      body:JSON.stringify(this.state),
      })
      .then(response=>response)
      .then((data)=>{
          if(data){
              this.fetchData();
          }
      });    
    //console.log(id)

  }

  render(){
    const trbData=this.state.data;
    const rows=trbData.map((trb)=>
        <tr key={trb.id_trb}>
        <th scope="row">{trb.id_trb}</th>
        <td> {trb.intitule_trb} </td>
        <td> {trb.resp_trb} </td>
        <td> 
            <Link to={'/update/'+trb.id_trb} className="btn btn-info" > Modifier </Link> 
            <button onClick={()=>this.deleteEmp(trb.id_trb)} className="btn btn-danger ml-3"> Supprimer </button>   
        </td>
      </tr>
    );

    return(
      <div>
      <Header />
      <table className="table table-striped">
      <thead>
        <tr>
          <th scope="col">Identité </th>
          <th scope="col">Intitulé</th>
          <th scope="col">responsable du Tribe </th>
        </tr>
      </thead>
      <tbody>
          {rows}
      </tbody>
      </table>

      </div>
  )
  }
}

export default ListTribes;
