import React from "react";
import { Link } from 'react-router-dom';
class Signup extends React.Component{

    constructor(){
        super();
        this.state={
            'username':'',
            'email':'',
            'mdp':''
        }
        this.changeHandler= this.changeHandler.bind(this);
        this.submitted= this.submitted.bind(this);
    }

    changeHandler(event){
        this.setState({
            [event.target.name]:event.target.value
        });
        console.log(event.target.name)
    }

    submitted(){
        fetch('http://127.0.0.1:8000/signup',
        {
            method:'POST',
            body:JSON.stringify(this.state),
            headers:{'Content-type': 'application/json; charset=UTF-8',}
        }
        )
        // Redirection vers la page racine
        this.props.history.push('/');
        // Rafraîchir la page après la redirection
        window.location.reload()        
    }

    render(){
        return(
            <div>
                <h2>Formulaire d'inscription RH</h2>
                    <form>
                    <div className="form-group">
                        <label htmlFor="username">Entrer votre username:</label>
                        <input  name="nomComplet" type="text" onChange={this.changeHandler} className="form-control" id="username" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Entrer votre émail:</label>
                        <input  name="email" type="text" onChange={this.changeHandler} className="form-control" id="email" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="mdp">Entrer votre mot de passe:</label>
                        <input  name="mdp" type="text" onChange={this.changeHandler} className="form-control" id="mdp" />
                    </div>
                    <div className="form-group row">
                    <div className="col-sm-10">
                    
                        <button type="submit" onClick={this.submitted} className="btn btn-primary">Ajouter un Employée</button>
                    
                        </div>
                    </div>                                              
                    </form>
            </div>
        )
    }
}

export default Signup;
