import React from "react";
import { Link } from "react-router-dom";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import "../../page.css";

class ListEquipe extends React.Component {
  constructor() {
    super();
    this.state = {
      data: [],
      emp: [], 
    };
  }

  fetchData() {
    fetch('http://127.0.0.1:8000/team/')
      .then(response => response.json())
      .then((data) => {
        this.setState({
          data: data,
        });
      });
  }

  // Add this function to fetch employee data
  fetchDataEmp() {
    fetch('http://127.0.0.1:8000/employee/')
      .then((response) => response.json())
      .then((data) => {
        const leaders = data
        this.setState({
          emp: leaders,
        });
        console.log(this.state.emp)
      });
  }

  componentDidMount() {
    this.fetchData();
    this.fetchDataEmp(); // Fetch employee data when the component mounts
  }

  deleteEquipe(id) {
    fetch('http://127.0.0.1:8000/team/' + id + '/', {
      method: 'DELETE',
    })
      .then(response => response)
      .then(() => {
        this.fetchData();
      });
  }

  render() {
    const squadData = this.state.data;
    const responsable = this.state.resp; // Assuming resp is an array of responsible employees

    const rows = squadData.map((sqd) => {
      const Employee = responsable.find((emp) => emp.id === sqd.emp_eq);

      return (
        <tr key={sqd.id_eq}>
          <th scope="row">{sqd.id_eq}</th>
          <th>{sqd.nom_eq}</th>
          <td> {sqd.resp_eq} </td>
          <td> {sqd.start_eq} </td>
          <td> {sqd.project_run} </td>
          <td> {Employee ? Employee.nomComplet : 'aucun'} </td>
          <td>
            <Link to={'/update_squad/' + sqd.id_eq} className="btn btn-info"> Modifier </Link>
            <button onClick={() => this.deleteEquipe(sqd.id_eq)} className="btn btn-danger ml-3"> Supprimer </button>
          </td>
        </tr>
      );
    });

    return (
      <div>
        <Header />
        <div id='page'>
          <Link className="nav-link" style={{ paddingLeft: '1340px' }} to="/add_équipe">
            <span className="btn btn-success" style={{ fontSize: '27px' }}>Ajouter</span>
          </Link>
          <table className="table table-striped table-bordered text-center">
            <thead>
              <tr>
                <th scope="row">Equipe No</th>
                <th> Nom équipe </th>
                <th> Responsable </th>
                <th> Date du création </th>
                <th> Projet en Cours </th>
                <th> Employées </th>
                <th> Actions </th>
              </tr>
            </thead>
            <tbody>
              {rows}
            </tbody>
          </table>
        </div>
        <Footer />
      </div>
    );
  }
}

export default ListEquipe;
