import React from "react";

class Signup extends React.Component {
    constructor() {
        super();
        this.state = {
            username: '',
            password: '',
            email: ''            
        };
        this.changeHandler = this.changeHandler.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    changeHandler(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

     
    handleSubmit(event) {
        event.preventDefault(); // Prevent the default form submission
    
        fetch('http://127.0.0.1:8000/signup/', {
            method: 'POST',
            body: JSON.stringify(this.state),
            headers: {
                'Content-Type': 'application/json', // Removed charset
            },
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json(); // Parse the JSON response
        })
        .then(data => {
            // Handle the success response data here
            console.log('Success:', data);
        })
        .catch(error => {
            // Handle errors here
            console.error('Error:', error);
        });
    }
        
        
    

    render() {
        return (
            <div>
                <h2>Formulaire d'inscription RH</h2>
                <form >
                    <div className="form-group">
                        <label htmlFor="username">Entrer votre username:</label>
                        <input name="username" type="text" onChange={this.changeHandler} className="form-control" id="username"  />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Entrer votre émail:</label>
                        <input name="email" type="text" onChange={this.changeHandler} className="form-control" id="email"  />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Entrer votre mot de passe:</label>
                        <input name="password" type="password" onChange={this.changeHandler} className="form-control" id="password"  />
                    </div>
                    <div className="form-group row">
                        <div className="col-sm-10">
                            <button type="submit" onClick={this.handleSubmit} className="btn btn-primary">Ajouter un Employée</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Signup;
