import React from "react";

class Signup extends React.Component {
    constructor() {
        super();
        this.state = {
            username: '',
            email: '',
            password: ''
        };
        this.changeHandler = this.changeHandler.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    changeHandler(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    async handleSubmit(event) {
        event.preventDefault();
        try {
            const response = await fetch('http://127.0.0.1:8000/signup', {
                method: 'POST',
                body: JSON.stringify(this.state),
                headers: {
                    'Content-type': 'application/json; charset=UTF-8',
                }
            });

            if (response.ok) {
                console.log('User registered successfully!');
                this.setState({
                    username: '',
                    email: '',
                    password: ''
                });
            } else {
                console.error('Registration failed.');
            }
        } catch (error) {
            console.error('An error occurred:', error);
        }
    }

    render() {
        return (
            <div>
                <h2>Formulaire d'inscription RH</h2>
                <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">Entrer votre username:</label>
                        <input name="username" type="text" onChange={this.changeHandler} className="form-control" id="username" value={this.state.username} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Entrer votre émail:</label>
                        <input name="email" type="text" onChange={this.changeHandler} className="form-control" id="email" value={this.state.email} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Entrer votre mot de passe:</label>
                        <input name="password" type="password" onChange={this.changeHandler} className="form-control" id="password" value={this.state.password} />
                    </div>
                    <div className="form-group row">
                        <div className="col-sm-10">
                            <button type="submit" className="btn btn-primary">Ajouter un Employée</button>
                        </div>
                    </div>
                </form>
            </div>
        );
    }
}

export default Signup;
