import React from "react";

class Update extends React.Component{

    constructor(){
        super();
        this.state={
            'nomComplet':'',
            'email':'',
            'contact':'',
            'adresse':''
        }
        this.changeHandler= this.changeHandler.bind(this);
        this.submitted= this.submitted.bind(this);
    }

    changeHandler(event){
        this.setState({
            [event.target.name]:event.target.value
        });
       // console.log(event.target.name)
    }

    fetchData(){
        var id=this.props.match.params.id;
        fetch('http://127.0.0.1:8000/employee/'+id+'/')
        .then(response=>response.json())
        .then((data)=>{
          this.setState({
            nomComplet:data.nomComplet,
            email:data.email,
            contact:data.contact,
            addresse:data.addresse
          });
          console.log(data)
        });
      }
    
      componentDidMount(){
        this.fetchData();
      }

    updateFct(event){
        this.setState({
            [event.target.name]:event.target.value
        });
       // console.log(event.target.name)
    }

    submitted(){
        var id=this.props.match.params.id;
        fetch('http://127.0.0.1:8000/employee/'+id+'/',
        {
            method:'PUT',
            body:JSON.stringify(this.state),
            headers:{'Content-type': 'application/json; charset=UTF-8',}
        }
        )
    }

    render(){
        return(
            <div>
                <h2>Formulaire de modification d'un Employée</h2>
                <form>
                    <div className="form-group">
                        <label htmlFor="email">Another label</label>
                        <input name='email' type="text" className="form-control" id="email" placeholder="ARRADI@gmail.com"/>
                   </div>
                    <div className="form-group">
                        <label htmlFor="email">Another label</label>
                        <input name='email' type="text" className="form-control" id="email" placeholder="ARRADI@gmail.com"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="contact">Another label</label>
                        <input name='contact' type="text" className="form-control" id="contact" placeholder="06 16 32 06 16"/>
                    </div>
                    <div className="form-group">
                        <label htmlFor="adr">Another label</label>
                        <input name='adresse' type="text" className="form-control" id="adr" placeholder="lotissement Amine / casablanca"/>
                    </div>  
                    <div className="form-group row">
                    <div className="col-sm-10">
                        <button  type="submit" className="btn btn-primary"> Modifier un Employé </button>
                        </div>
                    </div>                                              
                    </form>            
            </div>
        )
    }
}

export default Update ;
