import React from "react";
import Header  from "../components/Header";
class AddTribes extends React.Component{

    constructor(){
        super();
        this.state={
            'id_trb':'',
            'intitule_trb':'',
            'resp_trb':''           
        }
        this.changeHandler= this.changeHandler.bind(this);
        this.submitted= this.submitted.bind(this);
    }

    changeHandler(event){
        this.setState({
            [event.target.name]:event.target.value
        });
       // console.log(event.target.name)
    }

    submitted(){
        fetch('http://127.0.0.1:8000/tribe/',
        {
            method:'POST',
            body:JSON.stringify(this.state),
            headers:{'Content-type': 'application/json; charset=UTF-8',}
        }
        )
        // Redirection vers la page racine
        this.props.history.push('/');
        // Rafraîchir la page après la redirection
        window.location.reload()        
    }

    render(){
        return(
            
            <div>
                <Header />
                <h2>Formulaire d'ajout d'un Employée</h2>
                    <form>
                    <div className="form-group">
                        <label htmlFor="id_trb">Entrer identité du Tribe:</label>
                        <input  name="id_trb" type="text" onChange={this.changeHandler} className="form-control" id="id_trb" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="intitule_trb">Entrer l'intitulé:</label>
                        <input  name="intitule_trb" type="text" onChange={this.changeHandler} className="form-control" id="intitule_trb" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="resp_trb">Entrer votre contact:</label>
                        <input  name="resp_trb"type="text" onChange={this.changeHandler} className="form-control" id="resp_trb" />
                    </div>
 
                    <div className="form-group row">
                    <div className="col-sm-10">
                    
                        <button type="submit" onClick={this.submitted} className="btn btn-primary">Ajouter un Tribe</button>
                    
                        </div>
                    </div>                                              
                    </form>
            </div>
        )
    }
}

export default AddTribes;
