import React from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";

class List extends React.Component{

  constructor(){
    super();
    this.state={
      data:[]
    };
  }

  fetchData(){
    fetch('http://127.0.0.1:8000/employee/')
    .then(response=>response.json())
    .then((data)=>{
      this.setState({
        data:data 
      });
      console.log(data)
    });
  }

  componentDidMount(){
    this.fetchData();
  }

  deleteEmp(id){
    console.log(id)

  }

  render(){
    const empData=this.state.data;
    const rows=empData.map((emp)=>
        <tr key={emp.id}>
        <th scope="row">{emp.id}</th>
        <td> {emp.nomComplet} </td>
        <td> {emp.email} </td>
        <td> {emp.contact} </td>
        <td> {emp.adresse} </td>
        <td> 
            <Link to={'/update/'+emp.id} className="btn btn-info" > Modifier </Link> 
            <button onClick={this.deleteEmp} className="btn btn-danger ml-3"> Supprimer </button>   
        </td>
      </tr>
    );

    return(
      <div><table className="table table-striped">
      <thead>
        <tr>
          <th scope="col">Identité</th>
          <th scope="col">Nom Complet</th>
          <th scope="col">Email</th>
          <th scope="col">Contact</th>
          <th scope="col">Adresse</th>
          <th scope="col">Actions</th>
        </tr>
      </thead>
      <tbody>
          {rows}
      </tbody>
      </table>

      </div>
  )
  }
}

export default List;
