import React from "react";

class Update extends React.Component{

    constructor(){
        super();
        this.state={
            'nomComplet':'',
            'email':'',
            'contact':'',
            'adresse':''
        };

    }

    updateFct(){

    }

    submitted(){
        fetch('http://127.0.0.1:8000/employee/',
        {
            method:'PUT',
            body:JSON.stringify(this.state),
            headers:{'Content-type': 'application/json; charset=UTF-8',}
        }
        )
    }

    render(){
        return(
            <div>
                <h2>Formulaire de modification d'un Employée</h2>
                <form>
                    <div className="form-group">
                        <label for="nomComp">Entrer votre nom complet:</label>
                        <input onChange={this.updateFct} type="text" className="form-control" id="nomComp" placeholder="ARRADI Khadija"/>
                    </div>
                    <div className="form-group">
                        <label for="email">Another label</label>
                        <input onChange={this.updateFct} type="text" className="form-control" id="email" placeholder="ARRADI@gmail.com"/>
                    </div>
                    <div className="form-group">
                        <label for="contact">Another label</label>
                        <input onChange={this.updateFct} type="text" className="form-control" id="contact" placeholder="06 16 32 06 16"/>
                    </div>
                    <div className="form-group">
                        <label for="adr">Another label</label>
                        <input onChange={this.updateFct} type="text" className="form-control" id="adr" placeholder="lotissement Amine / casablanca"/>
                    </div>  
                    <div className="form-group row">
                    <div className="col-sm-10">
                        <button onClick={this.submitted} type="submit" className="btn btn-primary"> Modifier un Employé </button>
                        </div>
                    </div>                                              
                    </form>            
            </div>
        )
    }
}

export default Update ;
