import React from "react";
import { Link } from "react-router-dom/cjs/react-router-dom.min";
import Header from "../../components/Header";
import  Footer  from "../../components/Footer";
import "../../page.css"
class ListEquipe extends React.Component{

  constructor(){
    super();
    this.state={
      data:[]
    };
  }

  fetchData(){
    fetch('http://127.0.0.1:8000/team/')
    .then(response=>response.json())
    .then((data)=>{
      this.setState({
        data:data 
      });
      console.log(data)
    });
  }

  componentDidMount(){
    this.fetchData();
  }

  deleteCongé(id){
    fetch('http://127.0.0.1:8000/team/'+id+'/',{
      method:'DELETE',
      body:JSON.stringify(this.state),
      })
      .then(response=>response)
      .then((data)=>{
          if(data){
              this.fetchData();
          }
      });    
    //console.log(id)

  }

  render(){
    //'id_eq', 'nom_eq','resp_eq','start_eq','project_run','emp_eq'
    const squadData=this.state.data;
    const rows=squadData.map((sqd)=>
        <tr key={sqd.id_eq}>
        <th scope="row">{sqd.nom_eq}</th>
        <td> {sqd.resp_eq} </td>
        <td> {sqd.start_eq} </td>
        <td> {sqd.project_run} </td>
        <td> {sqd.emp_eq} </td>
        <td> 
            <Link to={'/update_squad/'+sqd.id_eq} className="btn btn-info" > Modifier </Link> 
            <button onClick={()=>this.deleteCongé(sqd.id_eq)} className="btn btn-danger ml-3"> Supprimer </button>   
        </td>
      </tr>
    );

    return(
      <div>
      <Header />

      <div id='page'>
      <Link className="nav-link" style={{ paddingLeft:'1340px'}} to="/add_congés">
        <span className="btn btn-success" style={{ fontSize: '27px'}}>Ajouter</span>
      </Link> 
      <table className="table table-striped table-bordered text-center">
      <thead>
        <tr>
        <th scope="row">employée ID</th>
        <th> Type du Congé </th>
        <th> Date Début </th>
        <th> Date Fin </th>
        <th> Durée du Congé </th>
        <th> justification/ Raison </th>
        <th> Approbateur </th>
        </tr>
      </thead>
      <tbody>
          {rows}
      </tbody>
      </table>
      </div>
      <Footer />
      </div>
  )
  }
}

export default ListEquipe;
