import React from "react";
import { Link } from "react-router-dom";
import Header from "../../components/Header";
import Footer from "../../components/Footer";
import "../../page.css";

class ListEquipe extends React.Component {
  constructor() {
    super();
    this.state = {
      data: [],
      emp: []
    };
  }

  fetchData() {
    fetch('http://127.0.0.1:8000/team/')
      .then(response => response.json())
      .then((data) => {
        this.setState({
          data: data,
        });
      });
  }


  

  componentDidMount() {
    this.fetchData();
    this.fetchDataEmp(); // Fetch employee data when the component mounts
  }

  deleteEquipe(id) {
    fetch('http://127.0.0.1:8000/team/' + id + '/', {
      method: 'DELETE',
    })
      .then(response => response)
      .then(() => {
        this.fetchData();
      });
  }

    // Add this function to fetch employee data
    fetchDataEmp() {
        fetch('http://127.0.0.1:8000/employee/')
          .then((response) => response.json())
          .then((data) => {
            
            this.setState({ emp: data }, 
            );
          });
      }
      //*********************** */

  render() {
    //const employees = this.state.emp
   // console.log(employees.map(emp)=>emp.id;)
    
   const listdata = this.state.data;
   const employees = this.state.emp; 
   //console.log(employees)
   const rows = listdata.map((sqd) => {
    var eq_emp = employees.find((emp) => {
        for (const id of sqd.emp_eq) {
          if (emp.id === id) {
            return emp.nomComplet;
          }
        }
      });
      eq_emp = employees
      .filter((emp) => sqd.emp_eq.includes(emp.id))
      .map((emp) => emp.nomComplet)
      .join(', ');
     console.log(eq_emp)
   //'id_eq', 'nom_eq','resp_eq','start_eq','project_run','emp_eq'
     return (
       <tr key={sqd.id_eq}>
         <th scope="row">{sqd.id_eq}</th>
         <td>{sqd.nom_eq}</td>
         <td>{sqd.resp_eq}</td>
         <td>{sqd.start_eq}</td>
         <td>{sqd.project_run}</td>
         <td>{eq_emp ? eq_emp.nomComplet : 'aucun'}</td>
         <td>
           <Link to={'/update_équipe/' + sqd.id_eq} className="btn btn-info">
             Modifier
           </Link>
           <button
             onClick={() => this.deleteTribe(sqd.id_eq)}
             className="btn btn-danger ml-3"
           >
             Supprimer
           </button>
         </td>
       </tr>
     );
   });

    return (
      <div>
        <Header />
        <div id='page'>
          <Link className="nav-link" style={{ paddingLeft: '1340px' }} to="/add_équipe">
            <span className="btn btn-success" style={{ fontSize: '27px' }}>Ajouter</span>
          </Link>
          <table className="table table-striped table-bordered text-center">
            <thead>
              <tr>
                <th scope="row">Equipe No</th>
                <th> Nom équipe </th>
                <th> Responsable </th>
                <th> Date du création </th>
                <th> Projet en Cours </th>
                <th> Employées </th>
                <th> Actions </th>
              </tr>
            </thead>
            <tbody>
              



            {rows}





            </tbody>
          </table>
        </div>
        <Footer />
      </div>
    );
  }
}

export default ListEquipe;
