import React, { Component } from "react";

class Signup extends Component {
    constructor() {
        super();
        this.state = {
            username: '',
            password: '',
            email: ''
        };
    }

    changeHandler = event => {
        const { name, value } = event.target;
        this.setState({ [name]: value });
    };

    handleSubmit = event => {
        event.preventDefault();

        const { username, password, email } = this.state;
        const formData = { username, password, email };

        fetch('http://127.0.0.1:8000/signup/', {
            method: 'POST',
            body: JSON.stringify(formData),
            headers: {
                'Content-Type': 'application/json'
            }
        })
        .then(response => {
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            console.log('Success:', data);
            // You can add additional actions here upon successful signup
        })
        .catch(error => {
            console.error('Error:', error);
            // Handle errors here
        });
    };

    render() {
        //const { username, password, email } = this.state;

        return (
            <div className="col-6 offset-6">
                <h2>Formulaire d'ajout d'un Employée</h2>
                    <form onSubmit={this.handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">Entrer votre nom complet:</label>
                        <input  name="username" type="text" onChange={this.changeHandler} className="form-control" id="username"/>
                    </div>

                    <div className="form-group">
                        <label htmlFor="email">Entrer votre émail:</label>
                        <input  name="email" type="email" onChange={this.changeHandler} className="form-control" id="email" />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Entrer votre mot de passe:</label>
                        <input  name="password" type="password" onChange={this.changeHandler} className="form-control" id="password" />
                    </div>                    
 
                    <div className="form-group row">
                    <div className="col-sm-10">
                    
                        <button type="submit" onClick={this.submitted} className="btn btn-primary">Ajouter un Employée</button>
                    
                        </div>
                    </div>                                              
                    </form>
            </div>            
        );
    }
}

export default Signup;
