import React from "react";
import {Link} from 'react-router-dom'
class Login extends React.Component {
    constructor() {
        super();
        this.state = {
            username: '',
            email: '',
            password: ''
        };
        this.changeHandler = this.changeHandler.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    changeHandler(event) {
        this.setState({
            [event.target.name]: event.target.value
        });
    }

    async handleSubmit(event) {
        event.preventDefault();
        try {
            const response = await fetch('http://127.0.0.1:8000/login', {
                method: 'POST',
                body: JSON.stringify(this.state),
                headers: {
                    'Content-type': 'application/json; charset=UTF-8',
                }
            });

            if (response.ok) {
                console.log('User login successfully!');
                this.setState({
                    username: '',
                    email: '',
                    password: ''
                });
            } else {
                console.error('login failed.');
            }
            // Redirection vers la page racine
            this.props.history.push('/');
            // Rafraîchir la page après la redirection
            window.location.reload() 
        } catch (error) {
            console.error('An error occurred:', error);
        }
    }

    render() {
        return (
            
            <div>
                <div style={{  paddingBottom:'170px',paddingTop:'50px', paddingLeft:'400px', paddingRight :'400px', textAlign:'center'}}>

                <form onSubmit={this.handleSubmit} >
                
                    <div className="col-6 offset-3">
                    <h2>Veuillez vous identifier pour accéder à votre Espace RH:</h2>
                    <div className="form-group">
                        <label htmlFor="username">Entrer votre username:</label>
                        <input name="username" type="text" onChange={this.changeHandler} className="form-control" id="username" value={this.state.username} />
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Entrer votre mot de passe:</label>
                        <input name="password" type="password" onChange={this.changeHandler} className="form-control" id="password" value={this.state.password} />
                    </div>
                    <div className="form-group row">
                        <div className="col-sm-10">
                            <button type="submit"  className="btn btn-primary">S'authentifier</button>
                        </div>
                    </div>

                
                
                    <Link className="nav-link" to="/signup">s'inscrire <span className="sr-only">(current)</span></Link>
                 
                </div>
                </form>
            </div>               
            </div>

        );
    }
}

export default Login;
